    <span class="token tag"><span class="token tag"><span class="token punctuation">&lt;</span>script</span><span class="token punctuation">></span></span><span class="token script"><span class="token language-javascript">
      <span class="token function">$</span><span class="token punctuation">(</span><span class="token keyword">function</span> <span class="token punctuation">(</span><span class="token punctuation">)</span> <span class="token punctuation">{</span>
        <span class="token function">$</span><span class="token punctuation">(</span><span class="token string">'#tab td'</span><span class="token punctuation">)</span><span class="token punctuation">.</span><span class="token function">click</span><span class="token punctuation">(</span><span class="token keyword">function</span> <span class="token punctuation">(</span><span class="token punctuation">)</span> <span class="token punctuation">{</span>
          <span class="token comment">//缓存当前触发点击事件的单元格对象</span>
          <span class="token keyword">var</span> td <span class="token operator">=</span> <span class="token function">$</span><span class="token punctuation">(</span><span class="token keyword">this</span><span class="token punctuation">)</span>
          <span class="token comment">//缓存单元格中的文本值</span>
          <span class="token keyword">var</span> oldValue <span class="token operator">=</span> td<span class="token punctuation">.</span><span class="token function">text</span><span class="token punctuation">(</span><span class="token punctuation">)</span>
          <span class="token comment">//阻止下一次点击事件</span>
          td<span class="token punctuation">.</span><span class="token function">on</span><span class="token punctuation">(</span><span class="token string">'click'</span><span class="token punctuation">,</span> <span class="token boolean">false</span><span class="token punctuation">)</span>
          <span class="token comment">//创建一个元素并包装为jquery对象</span>
          <span class="token keyword">var</span> input <span class="token operator">=</span> <span class="token function">$</span><span class="token punctuation">(</span><span class="token string">'&lt;input type="text">'</span><span class="token punctuation">)</span>
          <span class="token comment">//设置输入框样式</span>
          input<span class="token punctuation">.</span><span class="token function">css</span><span class="token punctuation">(</span><span class="token punctuation">{</span>
            <span class="token literal-property property">border</span><span class="token operator">:</span> <span class="token string">'0'</span><span class="token punctuation">,</span>
            <span class="token literal-property property">font</span><span class="token operator">:</span> td<span class="token punctuation">.</span><span class="token function">css</span><span class="token punctuation">(</span><span class="token string">'font'</span><span class="token punctuation">)</span><span class="token punctuation">,</span>
            <span class="token literal-property property">outline</span><span class="token operator">:</span> <span class="token string">'none'</span>
          <span class="token punctuation">}</span><span class="token punctuation">)</span>
          <span class="token comment">//设置输入框的宽高跟单元格保持一致</span>
          input<span class="token punctuation">.</span><span class="token function">height</span><span class="token punctuation">(</span>td<span class="token punctuation">.</span><span class="token function">height</span><span class="token punctuation">(</span><span class="token punctuation">)</span><span class="token punctuation">)</span>
          input<span class="token punctuation">.</span><span class="token function">width</span><span class="token punctuation">(</span>td<span class="token punctuation">.</span><span class="token function">width</span><span class="token punctuation">(</span><span class="token punctuation">)</span><span class="token punctuation">)</span>
          <span class="token comment">//将输入框的值设置为单元格中的文本值</span>
          input<span class="token punctuation">.</span><span class="token function">val</span><span class="token punctuation">(</span>oldValue<span class="token punctuation">)</span>

          <span class="token comment">//将单元格的文本置空</span>
          td<span class="token punctuation">.</span><span class="token function">text</span><span class="token punctuation">(</span><span class="token string">''</span><span class="token punctuation">)</span>
          <span class="token comment">//将后者追加到前者中</span>
          td<span class="token punctuation">.</span><span class="token function">append</span><span class="token punctuation">(</span>input<span class="token punctuation">)</span>
          <span class="token comment">//选中当前控件</span>
          input<span class="token punctuation">.</span><span class="token function">select</span><span class="token punctuation">(</span><span class="token punctuation">)</span>
          <span class="token comment">//聚焦当前控件</span>
          <span class="token comment">//input.focus();</span>
          <span class="token comment">//取消输入框的点击事件</span>
          input<span class="token punctuation">.</span><span class="token function">on</span><span class="token punctuation">(</span><span class="token string">'click'</span><span class="token punctuation">,</span> <span class="token boolean">false</span><span class="token punctuation">)</span>
          <span class="token comment">// input.appendTo(td); //将前者追加后者中</span>

          <span class="token comment">//为输入框元素绑定失去焦点事件</span>
          input<span class="token punctuation">.</span><span class="token function">on</span><span class="token punctuation">(</span><span class="token string">'blur'</span><span class="token punctuation">,</span> <span class="token keyword">function</span> <span class="token punctuation">(</span><span class="token punctuation">)</span> <span class="token punctuation">{</span>
            <span class="token comment">//获取value值</span>
            <span class="token keyword">let</span> v <span class="token operator">=</span> <span class="token function">$</span><span class="token punctuation">(</span><span class="token keyword">this</span><span class="token punctuation">)</span><span class="token punctuation">.</span><span class="token function">val</span><span class="token punctuation">(</span><span class="token punctuation">)</span>
            <span class="token comment">//移除输入框</span>
            <span class="token function">$</span><span class="token punctuation">(</span><span class="token keyword">this</span><span class="token punctuation">)</span><span class="token punctuation">.</span><span class="token function">remove</span><span class="token punctuation">(</span><span class="token punctuation">)</span>
            <span class="token comment">//将单元格文本设置为输入框的值</span>
            td<span class="token punctuation">.</span><span class="token function">text</span><span class="token punctuation">(</span>v<span class="token punctuation">)</span>
            <span class="token comment">//允许下一次点击事件触发</span>
            td<span class="token punctuation">.</span><span class="token function">on</span><span class="token punctuation">(</span><span class="token string">'click'</span><span class="token punctuation">,</span> <span class="token boolean">true</span><span class="token punctuation">)</span>
            <span class="token comment">//判断是否有改动,若无改动则不作任何处理</span>
            <span class="token keyword">if</span> <span class="token punctuation">(</span>v <span class="token operator">===</span> oldValue<span class="token punctuation">)</span> <span class="token punctuation">{</span>
              <span class="token keyword">return</span>
            <span class="token punctuation">}</span>
            <span class="token keyword">if</span> <span class="token punctuation">(</span><span class="token operator">!</span>window<span class="token punctuation">.</span><span class="token function">confirm</span><span class="token punctuation">(</span><span class="token string">'确认修改?'</span><span class="token punctuation">)</span><span class="token punctuation">)</span> <span class="token punctuation">{</span>
              <span class="token comment">//还原文本内容</span>
              td<span class="token punctuation">.</span><span class="token function">text</span><span class="token punctuation">(</span>oldValue<span class="token punctuation">)</span>
              <span class="token keyword">return</span> <span class="token boolean">false</span>
            <span class="token punctuation">}</span>
          <span class="token punctuation">}</span><span class="token punctuation">)</span>

          <span class="token comment">//为输入框绑定一个回车按键事件</span>
          input<span class="token punctuation">.</span><span class="token function">on</span><span class="token punctuation">(</span><span class="token string">'keydown'</span><span class="token punctuation">,</span> <span class="token keyword">function</span> <span class="token punctuation">(</span><span class="token parameter">e</span><span class="token punctuation">)</span> <span class="token punctuation">{</span>
            <span class="token comment">//判断是否按下的回车键(enter:13)</span>
            <span class="token keyword">if</span> <span class="token punctuation">(</span>e<span class="token punctuation">.</span>keyCode <span class="token operator">==</span> <span class="token number">13</span><span class="token punctuation">)</span> <span class="token punctuation">{</span>
              <span class="token comment">//触发当前对象的失焦事件</span>
              <span class="token function">$</span><span class="token punctuation">(</span><span class="token keyword">this</span><span class="token punctuation">)</span><span class="token punctuation">.</span><span class="token function">blur</span><span class="token punctuation">(</span><span class="token punctuation">)</span>
            <span class="token punctuation">}</span>
          <span class="token punctuation">}</span><span class="token punctuation">)</span>
        <span class="token punctuation">}</span><span class="token punctuation">)</span>
      <span class="token punctuation">}</span><span class="token punctuation">)</span>
    </span></span><span class="token tag"><span class="token tag"><span class="token punctuation">&lt;/</span>script</span><span class="token punctuation">></span></span>
  <span class="token tag"><span class="token tag"><span class="token punctuation">&lt;/</span>body</span><span class="token punctuation">></span></span>
<span class="token tag"><span class="token tag"><span class="token punctuation">&lt;/</span>html</span><span class="token punctuation">></span></span>
