            <span class="token class-name">String</span> nickname <span class="token operator">=</span> rs<span class="token punctuation">.</span><span class="token function">getString</span><span class="token punctuation">(</span><span class="token string">"_nickname"</span><span class="token punctuation">)</span><span class="token punctuation">;</span>
